import React, { useState, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import DataSidebar from './components/DataSidebar';
import MainContent from './components/MainContent';
import './App.css';

const App = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isDataSidebarOpen, setIsDataSidebarOpen] = useState(false);
  const [deliveries, setDeliveries] = useState([]);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const toggleDataSidebar = () => {
    setIsDataSidebarOpen(!isDataSidebarOpen);
  };

  return (
    <div className="App">
      <button 
        className={`toggle-btn ${isSidebarOpen ? 'open' : 'closed'}`} 
        onClick={toggleSidebar}
      >
        {isSidebarOpen ? '<<' : '>>'}
      </button>
      
      <button 
        className={`toggle-data-btn ${isDataSidebarOpen ? 'open' : 'closed'}`} 
        onClick={toggleDataSidebar}
      >
        {isDataSidebarOpen ? '>>' : '<<'}
      </button>
      
      {isSidebarOpen && <Sidebar setDeliveries={setDeliveries} />}
      
      {isDataSidebarOpen && <DataSidebar deliveries={deliveries} />}

      <MainContent 
        isSidebarOpen={isSidebarOpen} 
        isDataSidebarOpen={isDataSidebarOpen}
        deliveries={deliveries} 
        setDeliveries={setDeliveries} 
      />
    </div>
  );
};

export default App;
