import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [data, setData] = useState([]);
  const [newItem, setNewItem] = useState({ pickup: '', delivery: '', description: '' });
  const [updateItem, setUpdateItem] = useState({ id: '', pickup: '', delivery: '', description: '' });
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    const response = await axios.get('http://localhost:5000/api/data');
    setData(response.data);
  };

  const handleAddItem = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:5000/api/data', newItem);
    setNewItem({ pickup: '', delivery: '', description: '' });
    fetchData();
  };

  const handleUpdateItem = async (e) => {
    e.preventDefault();
    await axios.put(`http://localhost:5000/api/data/${updateItem.id}`, {
      pickup: updateItem.pickup,
      delivery: updateItem.delivery,
      description: updateItem.description,
    });
    setUpdateItem({ id: '', pickup: '', delivery: '', description: '' });
    fetchData();
  };

  const handleDeleteItem = async (id) => {
    await axios.delete(`http://localhost:5000/api/data/${id}`);
    fetchData();
  };

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className="App">
      <div className={`sidebar ${isSidebarOpen ? 'open' : 'closed'}`}>
        <button className="toggle-btn" onClick={toggleSidebar}>
          {isSidebarOpen ? '<<' : '>>'}
        </button>
        <h2>Add New Delivery</h2>
        <form onSubmit={handleAddItem}>
          <input
            type="text"
            placeholder="Pickup Location"
            value={newItem.pickup}
            onChange={(e) => setNewItem({ ...newItem, pickup: e.target.value })}
          />
          <input
            type="text"
            placeholder="Delivery Location"
            value={newItem.delivery}
            onChange={(e) => setNewItem({ ...newItem, delivery: e.target.value })}
          />
          <input
            type="text"
            placeholder="Description"
            value={newItem.description}
            onChange={(e) => setNewItem({ ...newItem, description: e.target.value })}
          />
          <button type="submit">Add Delivery</button>
        </form>

        <h2>Update Delivery</h2>
        <form onSubmit={handleUpdateItem}>
          <input
            type="text"
            placeholder="ID"
            value={updateItem.id}
            onChange={(e) => setUpdateItem({ ...updateItem, id: e.target.value })}
          />
          <input
            type="text"
            placeholder="Pickup Location"
            value={updateItem.pickup}
            onChange={(e) => setUpdateItem({ ...updateItem, pickup: e.target.value })}
          />
          <input
            type="text"
            placeholder="Delivery Location"
            value={updateItem.delivery}
            onChange={(e) => setUpdateItem({ ...updateItem, delivery: e.target.value })}
          />
          <input
            type="text"
            placeholder="Description"
            value={updateItem.description}
            onChange={(e) => setUpdateItem({ ...updateItem, description: e.target.value })}
          />
          <button type="submit">Update Delivery</button>
        </form>
      </div>

      <div className="main-content">
        <h1>TorontoGo Delivery Management</h1>
        <h2>Deliveries</h2>
        <ul>
          {data.map((item) => (
            <li key={item._id}>
              Pickup: {item.pickup} - Delivery: {item.delivery} - Description: {item.description}
              <button onClick={() => handleDeleteItem(item._id)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
