import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Switch, Link, Redirect } from 'react-router-dom';
import './App.css';

function App() {
  const [data, setData] = useState([]);
  const [newItem, setNewItem] = useState({ pickup: '', delivery: '', description: '' });
  const [updateItem, setUpdateItem] = useState({ id: '', pickup: '', delivery: '', description: '' });
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    checkAuthStatus();
    if (isAuthenticated) {
      fetchData();
    }
  }, [isAuthenticated]);

  const checkAuthStatus = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/auth-status', { withCredentials: true });
      setIsAuthenticated(response.data.isAuthenticated);
    } catch (error) {
      setIsAuthenticated(false);
    }
  };

  const fetchData = async () => {
    const response = await axios.get('http://localhost:5000/api/data', { withCredentials: true });
    setData(response.data);
  };

  const handleAddItem = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:5000/api/data', newItem, { withCredentials: true });
    setNewItem({ pickup: '', delivery: '', description: '' });
    fetchData();
  };

  const handleUpdateItem = async (e) => {
    e.preventDefault();
    await axios.put(`http://localhost:5000/api/data/${updateItem.id}`, {
      pickup: updateItem.pickup,
      delivery: updateItem.delivery,
      description: updateItem.description,
    }, { withCredentials: true });
    setUpdateItem({ id: '', pickup: '', delivery: '', description: '' });
    fetchData();
  };

  const handleDeleteItem = async (id) => {
    await axios.delete(`http://localhost:5000/api/data/${id}`, { withCredentials: true });
    fetchData();
  };

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const handleLogout = async () => {
    await axios.post('http://localhost:5000/api/logout', {}, { withCredentials: true });
    setIsAuthenticated(false);
  };

  return (
    <Router>
      <div className="App">
        <button className={`toggle-btn ${isSidebarOpen ? 'open' : 'closed'}`} onClick={toggleSidebar}>
          {isSidebarOpen ? '<<' : '>>'}
        </button>
        
        {isSidebarOpen && (
          <div className="sidebar">
            {isAuthenticated ? (
              <>
                <h2>Add New Delivery</h2>
                <form onSubmit={handleAddItem}>
                  <input
                    type="text"
                    placeholder="Pickup Location"
                    value={newItem.pickup}
                    onChange={(e) => setNewItem({ ...newItem, pickup: e.target.value })}
                  />
                  <input
                    type="text"
                    placeholder="Delivery Location"
                    value={newItem.delivery}
                    onChange={(e) => setNewItem({ ...newItem, delivery: e.target.value })}
                  />
                  <input
                    type="text"
                    placeholder="Description"
                    value={newItem.description}
                    onChange={(e) => setNewItem({ ...newItem, description: e.target.value })}
                  />
                  <button type="submit">Add Delivery</button>
                </form>

                <h2>Update Delivery</h2>
                <form onSubmit={handleUpdateItem}>
                  <input
                    type="text"
                    placeholder="ID"
                    value={updateItem.id}
                    onChange={(e) => setUpdateItem({ ...updateItem, id: e.target.value })}
                  />
                  <input
                    type="text"
                    placeholder="Pickup Location"
                    value={updateItem.pickup}
                    onChange={(e) => setUpdateItem({ ...updateItem, pickup: e.target.value })}
                  />
                  <input
                    type="text"
                    placeholder="Delivery Location"
                    value={updateItem.delivery}
                    onChange={(e) => setUpdateItem({ ...updateItem, delivery: e.target.value })}
                  />
                  <input
                    type="text"
                    placeholder="Description"
                    value={updateItem.description}
                    onChange={(e) => setUpdateItem({ ...updateItem, description: e.target.value })}
                  />
                  <button type="submit">Update Delivery</button>
                </form>
                <button onClick={handleLogout}>Logout</button>
              </>
            ) : (
              <div>
                <Link to="/login">Login</Link>
                <Link to="/signup">Sign Up</Link>
              </div>
            )}
          </div>
        )}

        <div className={`main-content ${isSidebarOpen ? 'sidebar-open' : 'sidebar-closed'}`}>
          <Switch>
            <Route exact path="/">
              {isAuthenticated ? (
                <>
                  <h1>TorontoGo Delivery Management</h1>
                  <h2>Deliveries</h2>
                  <ul>
                    {data.map((item) => (
                      <li key={item._id}>
                        Pickup: {item.pickup} - Delivery: {item.delivery} - Description: {item.description}
                        <button onClick={() => handleDeleteItem(item._id)}>Delete</button>
                      </li>
                    ))}
                  </ul>
                </>
              ) : (
                <Redirect to="/login" />
              )}
            </Route>
            <Route path="/login" component={Login} />
            <Route path="/signup" component={Signup} />
          </Switch>
        </div>
      </div>
    </Router>
  );
}

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/login', { email, password }, { withCredentials: true });
      window.location.href = '/';
    } catch (error) {
      console.error('Login failed', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" required />
      <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" required />
      <button type="submit">Login</button>
    </form>
  );
}

function Signup() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/signup', { email, password });
      window.location.href = '/login';
    } catch (error) {
      console.error('Signup failed', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" required />
      <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" required />
      <button type="submit">Sign Up</button>
    </form>
  );
}

export default App;
